function onLoad() {
   //Type appropriate comment here, and begin script below	
	//var myparm = getParmVal('pia_number');
	//alert(myparm);	
	//alert(this.location.href);
	var sysIdPIA = getParameterValue('pia');
	//if(sysIdPIA) {
	//	g_form.setValue('project_control_number', sysIdPIA);
	//}
		//Type appropriate comment here, and begin script below
     var ga = new GlideAjax('PIAUtils');	
	ga.addParam('sysparm_name', 'getFieldValue');
	ga.addParam('sysparm_PIASysId', sysIdPIA);
	ga.addParam('sysparm_PIAFieldName', 'project_control_number');
	ga.getXML(showFieldValue);

}


function getParameterValue(name) {
       name = name.replace(/[\[]/, "\\\[").replace(/[\]]/, "\\\]");
       var regexS = "[\\?&]" + name + "=([^&#]*)";
       var regex = new RegExp(regexS);	
       var results = regex.exec(top.location);
       if (results == null) {
               return "";
       } else {
               return unescape(results[1]);
       }
}

function showFieldValue(response) {
	var answer = response.responseXML.documentElement.getAttribute("answer");
	g_form.setValue('project_control_number', '');
	if (answer){
		g_form.setValue('project_control_number', answer);
	}	
}
