function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    //Type appropriate comment here, and begin script below
    if (newValue == 'true') {
        g_form.addInfoMessage("Showing Next section: Threshold Analysis, accomplish the section then Save the form");
		g_form.addInfoMessage("After completing Threshold Analysis, complete the Section 3");
        var tabIndex = g_tabs2Sections.findTabIndexByName('22_thresholdanalysis');
        g_tabs2Sections.setActive(tabIndex);
    }
}
