sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

	//Type appropriate comment here, and begin script below
    var ga = new GlideAjax('PIAUtils');	
	ga.addParam('sysparm_name', 'calculateRiskScore');
	ga.addParam('sysparm_Impact', g_form.getValue('impact'));
	ga.addParam('sysparm_Likelihood', newValue);
	ga.getXML(showFieldValue);
}

function showFieldValue(response) {
	var answer = response.responseXML.documentElement.getAttribute("answer");
	g_form.setValue('risk_score', '');
	if (answer){
		var jsonRisk = JSON.parse(answer);
		g_form.setValue('risk_score', jsonRisk.risk_score );
	}
	//g_form.setReadOnly('field_display', true);
}
