function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    //Type appropriate comment here, and begin script below
    if (newValue == 'true') {
        g_form.addInfoMessage("Showing Next section: Data lifecycle narrative");
		g_form.addInfoMessage("After completing Data lifecycle narrative accomplish Section 4.2 below");
        var tabIndex = g_tabs2Sections.findTabIndexByName('41_datalifecyclenarrative');
        g_tabs2Sections.setActive(tabIndex);
    }
}
