sys_script_email.script:
(function runMailScript( /* GlideRecord */ current, /* TemplatePrinter */ template,
    /* Optional EmailOutbound */
    email, /* Optional GlideRecord */ email_action,
    /* Optional GlideRecord */
    event) {

    // get the proj sys id then query rtp
    var prj = current.project.sys_id;
    var rtp;

    var gr = new GlideRecord('x_935283_pia_rtp');
    gr.addEncodedQuery('active=true^project=' + prj);
    gr.query();
    while (gr.next()) {
        rtp = gr.sys_id;
    }
    var link = 'https://dev139490.service-now.com/nav_to.do?uri=x_935283_pia_rtp.do?sys_id=' + rtp;
    template.print('<a href="' + link + '">RTP</a>');

})(current, template, email, email_action, event);
