sys_script_email.script:
(function runMailScript( /* GlideRecord */ current, /* TemplatePrinter */ template,
    /* Optional EmailOutbound */
    email, /* Optional GlideRecord */ email_action,
    /* Optional GlideRecord */
    event) {

    // get the proj sys id then query pam
    var prj = current.project.sys_id;
    var pam;

    var gr = new GlideRecord('x_935283_pia_pam');
    gr.addEncodedQuery('active=true^name_of_project=' + prj);
    gr.query();
    while (gr.next()) {
        pam = gr.sys_id;
    }
    var link = 'https://dev139490.service-now.com/nav_to.do?uri=x_935283_pia_pam.do?sys_id=' + pam;
    template.print('<a href="' + link + '">PAM</a>');

})(current, template, email, email_action, event);
