var newListUtil = Class.create();
newListUtil.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {


    /*
     * isInList - check if an id is in the list
     *
     * @param id - sys_id to look for
     * @return - boolean
     * @uses - this._list
     *
     */
    isInList: function(id, current) {


        // var i = this._getIndex(id);

        this._debug('isInList(' + i + '): looking for ' + id + ' in ' + "Is found");
        //this._debug('isInList(' + i + '): looking for ' + id + ' in ' + this.getList());
        for (var i = 0; i < current.length; i++) {

            if (current[i] == id)
                return i;
        }

    },
    /*
     * getList - display the list as CSV
     *
     * @param - NA
     * @return - string of CSV
     * @uses - this._list
     *
     */
    getList: function() {

        return this._list.join(',');
    },


    /*
     * _debug - print a message to the debug log
     *
     * @param s - string to print
     * @return - NA
     * @usrs - this._debug
     *
     */
    _debug: function(s) {

        if (this.debug)
            gs.print('>>>DEBUG: ListUtil: ' + s);
    },


    type: 'newListUtil'
});
