sys_script_include.script:
var pamUtils = Class.create();
pamUtils.prototype = {
    initialize: function() {
    },

	isHighSevere: function(pam){
		var query = "pam=" + pam  + "^risk_scoreINhigh,severe";
		var riskGR = new GlideRecord('x_935283_pia_risk');
		riskGR.addEncodedQuery(query);
		riskGR.query();
		if (!riskGR.next()){
			return true;
		}else return false;
	},

	isRiskPresent: function (pam){
		var riskGR = new GlideRecord('x_935283_pia_risk')
		riskGR.addEncodedQuery("pam=" + pam);
		riskGR.query();
		if (riskGR.next()){
			return true;
		}else return false;

	}
	,

	sectionRiskMapping: function(section){
		var riskEvnt = [];
		var query = "u_pam_section=" + section;
		var riskStatement = new GlideRecord('sn_risk_definition');		
		riskStatement.addEncodedQuery(query);
		riskStatement.query();
		while(riskStatement.next()){
			riskEvnt.push(riskStatement.sys_id.toString());
		}
		return "sys_idIN" + riskEvnt;


	},



    type: 'pamUtils'
};
